    public static long [] enPassantHash = {-4877274345781605429L, -3716503044051852831L, 3432897762080060965L, 4755077951127492217L, 5750107438139502205L, 4044641573500363339L, 3107183796848634575L, 8154128806490050771L};

    public static long computeHash(GameState gameState){
            long [] board = gameState.getBoard().getBitboard();
            long hash = 0L;
            for(int i = 0; i < 12; i++){
                long l = board[i];
                while(l != 0){
                    int index = Long.numberOfTrailingZeros(l);
                    hash ^= pieceHash[i][index];
                    l &= ~(1L << index);
                }
            }
            if(gameState.getTurn()){
                hash ^= turnHash;
            }
            hash ^= castleHash[gameState.getCastlingRights()];
            if(gameState.getCurrentEnPassantSquare() >= 0){
                hash ^= enPassantHash[gameState.getCurrentEnPassantSquare() % 8];
            }
        return hash;
    }
